SELECT tt.no_tt FROM inbox_subdit_registrasi_produksi inbox RIGHT JOIN kaseksi ON inbox.userid = kaseksi.userid RIGHT JOIN tt_registrasi_produksi tt ON inbox.no_tt = tt.no_tt LEFT JOIN pendaftar_registrasi_produksi pendaftar ON tt.kode_pendaftar = pendaftar.kode_pendaftar LEFT JOIN cek_1_alkes cek ON tt.no_tt = cek.no_tt LEFT JOIN surat_keputusan sk ON cek.id_cek_1 = sk.id_cek_1 LEFT JOIN kasubdit ON tt.kode_subdit = kasubdit.id_subdit WHERE kasubdit.id_subdit=5 AND sk.id_cek_1 IS NULL;
